mapper.select:
select seq, id, ref, step, depth, title, content, wdate, del, readcount
from bbs
where seq=#{seq}

select seq, id, content, wdate
from bbscomment 
where seq=#{seq}
order by wdate desc;

mapper.update:
update bbs
set title=#{title}, content=#{content}, wdate=now()
where seq=#{seq}

update bbs
set del=1
where seq=#{seq}
