mapper.select:
select seq, id, ref, step, depth, title, content, wdate, del, readcount, category
from qna
where seq=#{seq}

mapper.update:
update qna
set title=#{title}, content=#{content}, wdate=now()
where seq=#{seq}

update qna
set del=1
where seq=#{seq}

mapper.select:
select seq, id, content, wdate
from qnacomment 
where comseq=#{comseq}
order by wdate desc;
