mapper.select:
SELECT flight_id, flight_name, departure_airport, arrival_airport, departure_date, arrival_date, price
FROM flight_info
WHERE departure_airport=#{departure_airport} AND arrival_airport=#{arrival_airport} AND departure_date >= #{departure_date}

select seat_id, flight_id, seat_name, seat_price, is_available
from seat
where flight_id=#{flight_id}

mapper.update:
update seat
set is_available = 0
where seat_name = #{seatName} and flight_id = #{flight_id}

